import API from "../api";

const TodoList = ({ todos, fetchTodos }) => {
  const deleteTodo = async (id) => {
    await API.delete(`/todos/${id}`);
    fetchTodos();
  };

  return (
    <ul>
      {todos.map((todo) => (
        <li key={todo._id}>
          <strong>{todo.title}</strong> - {todo.description}
          <button onClick={() => deleteTodo(todo._id)}>‚ùå</button>
        </li>
      ))}
    </ul>
  );
};

export default TodoList;
